/*Table structure for table `god_novel` */

DROP TABLE IF EXISTS `god_novel`;

CREATE TABLE `god_novel` (
  `novelId` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `novelName` varchar(50) NOT NULL COMMENT '小说明',
  `parentId` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '父分类ID',
  `categoryId` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '分类ID',
  `categoryName` varchar(30) NOT NULL COMMENT '分类名',
  `author` varchar(30) NOT NULL COMMENT '作者',
  `picture` varchar(255) NOT NULL COMMENT '图片地址',
  `comment` varchar(255) NOT NULL COMMENT '简介',
  `views` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '查看数',
  `totals` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '总字数',
  `createdTime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `modifiedTime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`novelId`),
  KEY `parentId` (`parentId`,`novelId`),
  KEY `categoryId` (`categoryId`,`novelId`),
  KEY `views` (`categoryId`,`views`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

/*Data for the table `god_novel` */

LOCK TABLES `god_novel` WRITE;

insert  into `god_novel`(`novelId`,`novelName`,`parentId`,`categoryId`,`categoryName`,`author`,`picture`,`comment`,`views`,`totals`,`createdTime`,`modifiedTime`) values (1,'如意乾坤袋',6,34,'玄幻异幻','暮烟[签约作家]','data/picture/6/34/1289643365390.jpg','书籍简介：【凌霄阁】陈轩，国家安全局的情报组组长。在一次任务之后得到一只造型奇异的袋子――其实就是个储物袋。在携美国军事基地里的全部军火穿越到异界之后，从此陈轩的人生便发生了翻天覆地的变化，看军火怪胎如何在魔法师与武者共存的的世界里生存……',0,0,1289643365,1289643365),(2,'劫变宇宙',6,32,'中国科幻','远古剑仙[签约作家]','data/picture/6/32/1289643365412.jpg','书籍简介：劫难四起，烽烟不断，邪恶势力席卷而来，地球少年李峰，也被卷入了这场浩劫之中。宗门大战、物种大战、文明碰撞、科技、修士以及种类繁多的生物……直接的、间接的战争形式，各种争斗在广袤的宇宙中不断上演。看李峰如何荡平邪恶，走向强大!',0,0,1289643425,1289643425),(3,'圣器教尊',6,32,'中国科幻','宇化龙[签约作家]','data/picture/6/32/1289643425621.jpg','书籍简介：一个智慧型的青少年，穿越时空到洪荒时代，然后结识无数名人神怪之故事，回到现在再到未来，充满了神奇的故事让人着迷……',0,0,1289643426,1289643426),(4,'乱魔传',6,32,'中国科幻','干瘪的黄瓜[签约作家]','data/picture/6/32/1289643426210.jpg','书籍简介：修仙问道，他资质不好，无法完成父亲的夙愿。驱鬼抓魂，他一向在行，请看来自驱鬼世家的主人公纵横人界，成就大道的故事。',0,0,1289643426,1289643426),(5,'龙啸天',6,34,'玄幻异幻','夜想','data/picture/6/34/128964342658.jpg','书籍简介：一个拳手，一枚戒指，转世重生与另一片大陆，那是人兽混战的大陆……看他如何站在世界之巅……',4,0,1289643426,1289643426);

UNLOCK TABLES;

/*Table structure for table `god_novel_chapter` */

