DROP TABLE IF EXISTS ts_credit_setting;
CREATE TABLE `ts_credit_setting` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(50) NOT NULL,
  `alias` varchar(255) NOT NULL,
  `type` varchar(30) NOT NULL default 'user',
